import React, { useState } from "react";
import { useRecurrenceStore } from "../store/recurrenceStore";

export default function DatePickerUI() {
  const {
    startDate,
    endDate,
    setStartDate,
    setEndDate,
    exclusionDates,
    setExclusionDates,
    endAfterOccurrences,
    setEndAfterOccurrences,
    startTime,
    endTime,
    setStartTime,
    setEndTime,
  } = useRecurrenceStore();
  const [excludeInput, setExcludeInput] = useState("");
  const [endType, setEndType] = useState(endDate ? "date" : endAfterOccurrences ? "occurrences" : "date");

  const addExclusionDate = () => {
    if (excludeInput && !exclusionDates.includes(excludeInput)) {
      setExclusionDates([...exclusionDates, excludeInput]);
      setExcludeInput("");
    }
  };

  const removeExclusionDate = (date) => {
    setExclusionDates(exclusionDates.filter((d) => d !== date));
  };

  const handleEndTypeChange = (type) => {
    setEndType(type);
    if (type === "date") {
      setEndAfterOccurrences("");
    } else {
      setEndDate("");
    }
  };

  return (
    <div>
      <h2 className="font-semibold mb-2">Date Range</h2>
      <div className="flex items-center gap-4 mb-4">
        <div>
          <label className="block text-sm">Start Date</label>
          <input
            type="date"
            className="border rounded px-2 py-1"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">End Condition</label>
          <div className="flex items-center gap-2 mb-1">
            <label className="inline-flex items-center">
              <input
                type="radio"
                name="endType"
                value="date"
                checked={endType === "date"}
                onChange={() => handleEndTypeChange("date")}
              />
              <span className="ml-1 text-sm">End by date</span>
            </label>
            <input
              type="date"
              className="border rounded px-2 py-1 ml-2"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              disabled={endType !== "date"}
            />
          </div>
          <div className="flex items-center gap-2">
            <label className="inline-flex items-center">
              <input
                type="radio"
                name="endType"
                value="occurrences"
                checked={endType === "occurrences"}
                onChange={() => handleEndTypeChange("occurrences")}
              />
              <span className="ml-1 text-sm">End after</span>
            </label>
            <input
              type="number"
              min={1}
              className="border rounded px-2 py-1 w-20 ml-2"
              value={endAfterOccurrences}
              onChange={(e) => setEndAfterOccurrences(e.target.value.replace(/[^0-9]/g, ""))}
              disabled={endType !== "occurrences"}
            />
            <span className="text-sm">occurrences</span>
          </div>
        </div>
      </div>
      <div className="flex items-center gap-4 mb-4">
        <div>
          <label className="block text-sm">Start Time</label>
          <input
            type="time"
            className="border rounded px-2 py-1"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
          />
        </div>
        <div>
          <label className="block text-sm">End Time</label>
          <input
            type="time"
            className="border rounded px-2 py-1"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
          />
        </div>
      </div>
      <div className="mb-2">
        <label className="block text-sm font-medium mb-1">Exclusion Dates</label>
        <div className="flex items-center gap-2 mb-2">
          <input
            type="date"
            className="border rounded px-2 py-1"
            value={excludeInput}
            onChange={(e) => setExcludeInput(e.target.value)}
          />
          <button
            type="button"
            className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
            onClick={addExclusionDate}
            disabled={!excludeInput || exclusionDates.includes(excludeInput)}
          >
            Add
          </button>
        </div>
        <div className="flex flex-wrap gap-2">
          {exclusionDates.map((date) => (
            <span key={date} className="inline-flex items-center bg-gray-200 rounded px-2 py-1 text-xs">
              {date}
              <button
                type="button"
                className="ml-1 text-red-600 hover:text-red-800"
                onClick={() => removeExclusionDate(date)}
                aria-label={`Remove ${date}`}
              >
                ×
              </button>
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}
