import React from "react";
import { useRecurrenceStore } from "../store/recurrenceStore";

const frequencies = ["Daily", "Weekly", "Monthly", "Yearly"];
const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
const patterns = ["none", "first", "second", "third", "fourth", "last"];

export default function RecurrenceOptions() {
  const {
    frequency,
    interval,
    weekdays: selectedWeekdays,
    pattern,
    setFrequency,
    setInterval,
    setWeekdays,
    setPattern,
  } = useRecurrenceStore();

  const handleWeekdayChange = (idx) => {
    if (selectedWeekdays.includes(idx)) {
      setWeekdays(selectedWeekdays.filter((w) => w !== idx));
    } else {
      setWeekdays([...selectedWeekdays, idx]);
    }
  };

  return (
    <div>
      <h2 className="font-semibold mb-2">Recurrence Options</h2>
      <div className="flex items-center gap-2 mb-2">
        <span>Repeat:</span>
        <select
          className="border rounded px-2 py-1"
          value={frequency}
          onChange={(e) => setFrequency(e.target.value)}
        >
          {frequencies.map((freq) => (
            <option key={freq} value={freq.toLowerCase()}>{freq}</option>
          ))}
        </select>
        <span>every</span>
        <input
          type="number"
          min={1}
          value={interval}
          onChange={(e) => setInterval(Number(e.target.value))}
          className="w-16 border rounded px-2 py-1"
        />
        <span>time(s)</span>
      </div>
      <div className="mb-2">
        <span className="mr-2">On:</span>
        {weekdays.map((day, idx) => (
          <label key={day} className="inline-flex items-center mr-2">
            <input
              type="checkbox"
              className="form-checkbox"
              checked={selectedWeekdays.includes(idx)}
              onChange={() => handleWeekdayChange(idx)}
            />
            <span className="ml-1 text-sm">{day}</span>
          </label>
        ))}
      </div>
      <div>
        <span className="mr-2">Pattern:</span>
        <select
          className="border rounded px-2 py-1"
          value={pattern}
          onChange={(e) => setPattern(e.target.value)}
        >
          {patterns.map((p) => (
            <option key={p} value={p}>{p.charAt(0).toUpperCase() + p.slice(1)}</option>
          ))}
        </select>
        <span className="ml-2">weekday of the month</span>
      </div>
    </div>
  );
}
