import React from "react";
import { useRecurrenceStore } from "../store/recurrenceStore";

export default function DatePickerUI() {
  const { startDate, endDate, setStartDate, setEndDate } = useRecurrenceStore();
  return (
    <div>
      <h2 className="font-semibold mb-2">Date Range</h2>
      <div className="flex items-center gap-4">
        <div>
          <label className="block text-sm">Start Date</label>
          <input
            type="date"
            className="border rounded px-2 py-1"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </div>
        <div>
          <label className="block text-sm">End Date (optional)</label>
          <input
            type="date"
            className="border rounded px-2 py-1"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
}
